document.addEventListener("DOMContentLoaded",function(){for(var e=document.querySelector(".burger-btn"),t=document.querySelector(".nav__box"),o=document.querySelector(".nav"),n=document.querySelectorAll(".q"),c=document.querySelectorAll(".a"),r=document.querySelectorAll(".arrow"),l=(e.addEventListener("click",function(){t.classList.toggle("active")}),0);l<n.length;l++)(e=>{n[e].addEventListener("click",function(){c[e].classList.toggle("a-opened"),r[e].classList.toggle("arrow-rotated")})})(l);window.addEventListener("scroll",function(){100<=window.scrollY?o.classList.add("shadow-bg"):o.classList.remove("shadow-bg")})});